var reader = bufio.NewReader(strings.NewReader(input))

func main() {
	cave := make([][]string, 200)
	for i := range cave {
		cave[i] = make([]string, 1000)
		for j := range cave[i] {
			cave[i][j] = "."
		}
	}

	highestY := 0

	for {
		line, ok := readLine()
		if !ok {
			break
		}

		content := strings.Split(line, " -> ")
		for i := 1; i < len(content); i++ {
			coordinates1 := strings.Split(content[i-1], ",")
			x1, err := strconv.Atoi(coordinates1[0])
			if err != nil {
				panic(err)
			}

			y1, err := strconv.Atoi(coordinates1[1])
			if err != nil {
				panic(err)
			}

			coordinates2 := strings.Split(content[i], ",")
			x2, err := strconv.Atoi(coordinates2[0])
			if err != nil {
				panic(err)
			}

			y2, err := strconv.Atoi(coordinates2[1])
			if err != nil {
				panic(err)
			}

			if x1 == x2 {
				// vertical line
				if y1 > y2 {
					y1, y2 = y2, y1
				}

				for j := y1; j <= y2; j++ {
					cave[j][x1] = "#"
				}

				continue
			}

			if y1 == y2 {
				// horizontal line
				if x1 > x2 {
					x1, x2 = x2, x1
				}

				for j := x1; j <= x2; j++ {
					cave[y1][j] = "#"
				}
			}

			// part2
			if y2 > highestY {
				highestY = y2
			}
		}
	}

	cave[0][500] = "+"

	// draw cave floor
	for i := range cave[highestY+2] {
		cave[highestY+2][i] = "#"
	}
	//printCave(cave)

	sourceX := 500
	sourceY := 0

	// start sand flow
	sands := 0
	//reachedAbyss := false
	// part2
	reachedTop := false
	for !reachedTop {
		sandX, sandY := sourceX, sourceY
		for {
			// part 2 condition
			if cave[sourceY][sourceX] == "o" {
				printCave(cave)
				reachedTop = true
				fmt.Println("sands count:", sands)
				break
			}
			if sandX+1 == len(cave[0]) {
				fmt.Println("shit")
			}

			//if sandY+1 == len(cave) {
			//	//printCave(cave)
			//	fmt.Println("sands count:", sands)
			//	reachedAbyss = true
			//	break
			//}
			if cave[sandY+1][sandX] == "." {
				sandY++
			} else if cave[sandY+1][sandX-1] == "." {
				sandX--
				sandY++
			} else if cave[sandY+1][sandX+1] == "." {
				sandX++
				sandY++
			} else {
				cave[sandY][sandX] = "o"
				sands++
				break
			}
		}
	}
}

func printCave(cave [][]string) {
	for j := 0; j < 3; j++ {
		fmt.Printf("    ")
		for i := range cave[0] {
			if j == 0 {
				fmt.Printf("%d", i/100)
			} else if j == 1 {
				fmt.Printf("%d", (i%100)/10)
			} else {
				fmt.Printf("%d", i%10)
			}
		}

		fmt.Println()
	}

	for i := range cave {
		fmt.Printf("%3d ", i)
		for j := range cave[i] {
			fmt.Printf("%s", cave[i][j])
		}

		fmt.Println()
	}
}
func readLine() (string, bool) {
	line, err := reader.ReadString('\n')
	if err != nil {
		panic(err)
	}

	line = strings.TrimRight(line, "\n")

	if strings.Contains(line, "an") {
		return "", false
	}

	return line, true
}
